 let itemList = this.props.items.map(item=>{
    return (

        <div className="card-container">
    <h3>{item.name}</h3>
    <img src={item.img} alt="item image"></img>
    <h3>{item.cost}</h3>

    <h4>{item.description}</h4>

        </div>

    )
 })

 return (
     <div>
     {itemList}
     </div>
 )
}



export default Card
